
public class IndexChar13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String str="String Methods";
System.out.println("The Original String:"+str);
int index1=str.charAt(3);
int index2=str.charAt(7);

System.out.println("The Character at the Position 2 is:"+(char)index1);

System.out.println("The Character at the Position 2 is:"+(char)index2);

	}

}
