package com.assignmentOperator;

public class Assignmentoprex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;  
		a+=20; 
		System.out.println(a);  
		a-=10;  
		System.out.println(a);  
		a*=10;  
		System.out.println(a);  
		a/=2;  
		System.out.println(a); 
		a%=2;  
		System.out.println(a); 

	}

}
